import { atomFamily } from 'recoil';

export interface Page {
  rowsPerPage: number;
  page: number;
  totalRows: number;
}

export const pageState = atomFamily<Page, string>({
  key: 'table-page',
  default: {
    page: 0,
    rowsPerPage: 10,
    totalRows: 0,
  },
});
