import { atomFamily, selectorFamily } from 'recoil';
import { dataState } from './data';

export interface Page {
  rowsPerPage: number;
  page: number;
}

export const pageState = atomFamily<Page, string>({
  key: 'table-page',
  default: {
    page: 0,
    rowsPerPage: 10,
  },
});
