import { selectorFamily } from 'recoil';
import { dataState } from './data';

export const rowSelector = selectorFamily<any, any>({
  key: 'select-table-rows',
  get:({ tableKey }) =>
    ({ get }) =>
      get(dataState(tableKey)),
});
